mapper.select:
SELECT DISTINCT t1.ID_ AS taskId, t1.NAME_ AS taskName, t2.FIRST_ AS username, t1.PRIORITY_ as priority, t2.ID_ AS approverId, t3.NAME_ AS formName, t3.BUSINESS_KEY_ AS businessKey, t3.PROC_INST_ID_ AS processInstanceId, t3.TENANT_ID_ AS tenantId, t4.START_TIME_ AS startTime, t4.`DURATION_` as duration, t4.END_TIME_ AS endTime, t6.`apply_type` AS applyType, t6.`prefix_url` AS prefixUrl FROM act_hi_taskinst t1 LEFT JOIN act_hi_procinst t3 ON t1.PROC_INST_ID_ = t3.PROC_INST_ID_ LEFT JOIN `act_hi_actinst` t4 ON(t1.PROC_INST_ID_ = t4.PROC_INST_ID_ AND t4.`TASK_ID_` = t1.`ID_`) LEFT JOIN `act_hi_comment` t5 ON(t5.`TASK_ID_` = t1.`ID_` AND t5.`USER_ID_` IS NOT NULL) LEFT JOIN act_id_user t2 ON t5.`USER_ID_` = t2.ID_ LEFT JOIN `bpm_task_from` t6 ON t6.`process_definition_key` = t1.`PROC_DEF_ID_` WHERE t1.END_TIME_ IS NOT NULL
AND t1.ASSIGNEE_ = #{userId}
and ${sql_filter}
