mapper.select:
SELECT t.CATEGORY_ AS category, t.ID_ AS id, t.NAME_ AS name, t.KEY_ AS modelKey, t.RESOURCE_NAME_ AS resourceName, t.DGRM_RESOURCE_NAME_ as dgrmResourceName, t.SUSPENSION_STATE_ as suspensionState, t.VERSION_ AS version, t.DEPLOYMENT_ID_ AS deploymentId, t.TENANT_ID_ AS tenantId FROM act_re_procdef t
and t.NAME_ LIKE CONCAT('%',#{name},'%')
or t.KEY_ LIKE CONCAT('%',#{modelKey},'%')
or t.CATEGORY_ = #{category}
or t.SUSPENSION_STATE_ = #{suspensionState}
and ${sql_filter}
