mapper.update:
UPDATE sys_user
name = #{name,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, comp_id = #{compId,jdbcType=INTEGER}, dept_id = #{deptId,jdbcType=INTEGER}, card_id = #{cardId,jdbcType=VARCHAR}, avatar = #{avatar,jdbcType=VARCHAR}, update_by = #{updateBy,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, update_time = sysdate()
WHERE id = #{id}

UPDATE sys_user
password = #{password}, salt = #{salt}, pwd_security_level = #{pwdSecurityLevel}, pwd_update_date = sysdate()
WHERE id = #{id}

UPDATE sys_user
avatar = #{avatar,jdbcType=VARCHAR}, update_time = sysdate()
WHERE id = #{id}

mapper.select:
SELECT DISTINCT rm.menu_id FROM sys_user_role ur
	LEFT JOIN sys_role_menu rm ON ur.role_id = rm.role_id
WHERE ur.user_id = #{id}

WHERE u.del_flag = '0'
AND u.username LIKE '%,'||#{username}||',%' CONCAT('%', #{username}, '%')
AND u.status = #{status}
AND u.mobile LIKE '%,'||#{mobile}||',%' CONCAT('%', #{mobile}, '%')
AND u.email LIKE '%,'||#{email}||',%' CONCAT('%', #{email}, '%')
AND FIND_IN_SET(#{compId},d.parent_ids) OR (u.dept_id = #{compId} AND u.del_flag = '0')
AND u.dept_id = #{deptId}
AND ${sql_filter}
GROUP BY u.id,u.username, u.name,u.password,u.salt,u.email,u.mobile,u.status,u.card_id,u.create_time,u.dept_id,u.comp_id,c.name GROUP BY u.id

WHERE u.del_flag = '0'
AND u.username LIKE '%,'||#{username}||',%' CONCAT('%', #{username}, '%')
AND u.status = #{status}
AND u.mobile LIKE '%,'||#{mobile}||',%' CONCAT('%', #{mobile}, '%')
AND u.email LIKE '%,'||#{email}||',%' CONCAT('%', #{email}, '%')
AND u.comp_id = #{compId}
AND ${sql_filter}
GROUP BY u.id,u.username, u.name,u.password,u.salt,u.email,u.mobile,u.status,u.card_id,u.create_time,u.dept_id,u.comp_id,c.name GROUP BY u.id

WHERE u.del_flag = '0' AND r.role_id = #{roleId}
AND u.username LIKE '%,'||#{username}||',%' CONCAT('%', #{username}, '%')
AND u.status = #{status}
AND u.mobile LIKE '%,'||#{mobile}||',%' CONCAT('%', #{mobile}, '%')
AND u.email LIKE '%,'||#{email}||',%' CONCAT('%', #{email}, '%')
AND FIND_IN_SET(#{compId},d.parent_ids) OR (u.dept_id = #{compId} AND u.del_flag = '0')
AND ${sql_filter}
GROUP BY u.id,u.username, u.name,u.password,u.salt,u.email,u.mobile,u.status,u.card_id,u.create_time,u.dept_id,u.comp_id,c.name GROUP BY u.id

